#!/usr/bin/env node

"use strict";

const path = require('path');
const fs = require('fs');

// Check if alltest.js exists
const alltestPath = path.join(__dirname, '..', 'alltest.js');

if (fs.existsSync(alltestPath)) {
  // Execute alltest.js directly
  require(alltestPath);
} else {
  console.error('Error: alltest.js not found!');
  console.error('This CLI depends on alltest.js to function properly.');
  process.exit(1);
} 